    @test_throws AssertionError ce.c3 = ce.c2
    @test_throws AssertionError ce.c3 = 0.25
    ce.c3 = 0.6
    @test_throws AssertionError ce.c2 = 0.6
    @test ce.c3 == 0.6

    ce = CorSB1()
